import * as React from "react";

import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";

import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import { Activity } from "../backend/ts/common_define";
import { get_activity_templates } from "../backend/ts/database";
import {
  Drawer,
  List,
  Card,
  CardContent,
  Typography,
  IconButton,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider
} from "@material-ui/core";
import { useState } from "react";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForward";

interface Drawer_Component_Props {
  handleClick: Function;
}

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: "flex"
    },
    container: {
      display: "flex",
      flexWrap: "wrap"
    },
    button: {
      margin: theme.spacing(1)
    },
    textField: {
      margin: theme.spacing(1),
      width: "100%"
    },
    paper: {
      padding: theme.spacing(2),
      margin: theme.spacing(1)
    },
    card: {
      "&:hover": {
        boxShadow: "0 5px 15px rgba(0,0,0,0.3)"
      }
    },
    drawer: {
      width: 240,
      flexShrink: 0
    },
    drawerPaper: {
      width: 240
    },
    toolbar: theme.mixins.toolbar
  })
);

function Drawer_Component(props: Drawer_Component_Props) {
  const classes = useStyles({});
  const [state, setState] = useState({
    activities: new Array<Activity>()
  });

  React.useEffect(() => {
    get_activity_templates().then(value => {
      console.log(value)
      setState({
        activities: value
      });
    });
  }, []);

  return (
    <div>
      <Drawer
        className={classes.drawer}
        variant="permanent"
        classes={{
          paper: classes.drawerPaper
        }}
      >
        <div className={classes.toolbar} />
        <List>
          {state.activities &&
            state.activities.length > 0 &&
            state.activities.map((activity, index) => (
              <Grid key={`activity.${index}`} item>
                <Divider />
                <ListItem button key={index} onClick={() => props.handleClick(activity)}>
                  <ListItemText>{activity.title}</ListItemText>
                  <ListItemIcon>
                    <ArrowForwardIosIcon />
                  </ListItemIcon>
                </ListItem>
              </Grid>
            ))}
        </List>
      </Drawer>
    </div>
  );
}

export default Drawer_Component;
