import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { ProcessInstance } from '../backend/ts/common_define'
import { process_instance_update_step } from '../backend/ts/database'

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      width: '30%',
    },
    button: {
      marginTop: theme.spacing(1),
      marginRight: theme.spacing(1),
    },
    actionsContainer: {
      marginBottom: theme.spacing(2),
    },
    resetContainer: {
      padding: theme.spacing(3),
    },
  }),
);

interface Line_Stepper_Props {
  process: ProcessInstance;
}
//TODO maybe just use higher component state for active step?
export default function VerticalLinearStepper(props: Line_Stepper_Props) {
  const classes = useStyles({});
  //init active step to props.process.step
  const [activeStep, setActiveStep] = React.useState(props.process.step);
  const amountOfSteps = props.process.activities.length;

  const update_step_counter = (current_step: number) => {
    process_instance_update_step(props.process, current_step);
  }

  const getStepContent = (step: number) => {
    return props.process.activities[step].description;
  }

  const handleNext = () => {
    update_step_counter(activeStep + 1)
    setActiveStep(prevActiveStep => prevActiveStep + 1);
  };

  const handleBack = () => {
    update_step_counter(activeStep - 1)
    setActiveStep(prevActiveStep => prevActiveStep - 1);
  };

  const handleReset = () => {
    update_step_counter(0)
    setActiveStep(0);
  };

  return (
    <div className={classes.root}>
      <Stepper activeStep={activeStep} orientation="vertical">
        {props.process.activities.map((activity, index) => (
          <Step key={activity.title}>
            <StepLabel>{activity.title}</StepLabel>
            <StepContent>
              <Typography>{getStepContent(index)}</Typography>
              <div className={classes.actionsContainer}>
                <div>
                  <Button
                    disabled={activeStep === 0}
                    variant="contained"
                    color="secondary"
                    onClick={handleBack}
                    className={classes.button}
                  >
                    Tillbaka
                    </Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleNext}
                    className={classes.button}
                  >
                    {activeStep === amountOfSteps - 1|| amountOfSteps === 1 ? 'Klar' : 'Nästa'}
                  </Button>
                </div>
              </div>
            </StepContent>
          </Step>
        ))}
      </Stepper>
      {activeStep === amountOfSteps && (
        <Paper square elevation={0} className={classes.resetContainer}>
          <Typography>Alla steg är avklarade, bra jobbat</Typography>
          <Button
            onClick={handleReset}
            className={classes.button}
            variant="contained"
            style={{ backgroundColor: '#33cc33' }}
          >
            Återställ
            </Button>
        </Paper>
      )}
    </div>

  );
}
