import React, { useState } from "react";
import { Process, ProcessInstance } from "../backend/ts/common_define";
import { get_process_instances } from "../backend/ts/database";
import { Card, Grid, CardContent, Typography, createStyles, makeStyles, Theme, ButtonBase, CardActions, Button, ButtonGroup, IconButton } from '@material-ui/core/';
import { ThemeProvider } from "@material-ui/styles";
import with_on_hover_shadow from '../components/Shadow_On_Hover';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useHistory
} from "react-router-dom";
import GetNameModalButton from '../components/Get_Name_Modal';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';


interface View_Process_State {
  processes: Array<Process>;
}
const Card_HoverShadow = with_on_hover_shadow(Card, { init: 1, hovered: 3 });

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    fab: {
      margin: theme.spacing(1)
    },
    card: {
      height: "100%",
      "&:hover": {
        boxShadow: "0 5px 15px rgba(0,0,0,0.3)"
      }
    }
  })
);

function View_Process() {
    const [state, setState] = useState({
        processes: new Array<ProcessInstance>(),
    });

    const history = useHistory();

    const handleOpenClick= () => {
        history.push('/View_Process_Instance')
    };

    get_process_instances().then((value) => {
        setState({
            processes: value,
        });
    });
    const classes = useStyles({});

    return (
        <div>
            <Grid
                container
                spacing={3}
            >
                {state.processes && state.processes.length > 0 ? (
                    state.processes.map((process, index) => (
                        <Grid key={`process.card.${index}`} item xs={3}>
                            <Card
                                className={classes.card}
                            >
                                <CardContent>
                                    <Typography variant="h5">
                                        {process.title}
                                    </Typography>
                                    <Typography >
                                        {process.user_name}
                                    </Typography>
                                </CardContent>
                                <CardActions>

                                    <Grid
                                        container
                                        justify="center"
                                    >
                                            <IconButton color="secondary">
                                                <DeleteIcon />
                                            </IconButton>
                                             <IconButton onClick={handleOpenClick}>
                                                <ArrowForwardIosIcon/>
                                            </IconButton >
                                    </Grid>
                                </CardActions>
                            </Card>
                        </Grid>
            )))
          : null}
      </Grid>
    </div>
  );
}

export default View_Process;
