import React, { useState } from "react";
import { Process, ProcessInstance } from "../backend/ts/common_define";
import { get_process_instances, delete_process_instance } from "../backend/ts/database";
import { Card, Grid, CardContent, Typography, createStyles, makeStyles, Theme, CardActions, IconButton } from '@material-ui/core/';
import with_on_hover_shadow from '../components/Shadow_On_Hover';
import {
    useHistory
} from "react-router-dom";
import DeleteButton from "../components/Delete_Item_Modal";
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';


interface View_Process_State {
    processes: Array<Process>;
}
const Card_HoverShadow = with_on_hover_shadow(Card, { init: 1, hovered: 3 });

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        fab: {
            margin: theme.spacing(1)
        },
        card: {
            "&:hover": {
                boxShadow: "0 5px 15px rgba(0,0,0,0.3)"
            }
        }
    })
);

function View_Process() {
    const [state, setState] = useState({
        processes: new Array<ProcessInstance>(),
    });

    const history = useHistory();

    const handleOpenClick = (process: ProcessInstance) => {
        history.push(`/View_Process_Instance/${process.user_name}/${process.title}`);
    };

    const handleDelete = (process: ProcessInstance) => {
        setState({ 
            processes: state.processes.filter(p => p !== process) 
        })
        delete_process_instance(process);
    }

    React.useEffect(() => {
        get_process_instances().then((value) => {
            setState({
                processes: value,
            });
        });

    }, []);
    const classes = useStyles({});

    return (
        <div>
            <Grid
                container
                spacing={3}
            >
                {state.processes && state.processes.length > 0 ? (
                    state.processes.map((process, index) => (
                        <Grid key={`process.card.${index}`} item xs={12} sm={6} md={4} lg={3}>
                            <Card
                                className={classes.card}
                            >
                                <CardContent>
                                    <Typography variant="h5">
                                        {process.title}
                                    </Typography>
                                    <Typography >
                                        {process.user_name}
                                    </Typography>
                                    <Typography>{process.step}/{process.activities.length} completed</Typography>
                                </CardContent>
                                <CardActions>

                                    <Grid
                                        container
                                        justify="center"
                                    >
                                        <DeleteButton item={process} onDelete={handleDelete} />
                                        <IconButton onClick={() => { handleOpenClick(process) }}>
                                            <ArrowForwardIosIcon />
                                        </IconButton >
                                    </Grid>
                                </CardActions>
                            </Card>
                        </Grid>
                    )))
                    : null}
            </Grid>
        </div>
    );
}

export default View_Process;
