import * as React from "react";
import { withFormik, FormikProps, Form, FieldArray, Formik } from "formik";

import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from "@material-ui/core/DialogActions";
import DialogTitle from "@material-ui/core/DialogTitle";
import IconButton from '@material-ui/core/IconButton';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import { Process, Activity } from "../backend/ts/common_define";
import { save_process_template, does_process_exist } from "../backend/ts/database";
import { useHistory } from "react-router";
import { DialogContent } from "@material-ui/core";
import { useState } from "react";

interface OtherProps {
	title?: string;
}

interface CreateProcessProps {
	title?: string;
	activities?: Array<Activity>;
}

const useStyles = makeStyles((theme: Theme) =>
	createStyles({
		container: {
			display: 'flex',
			flexWrap: 'wrap',
		},
		button: {
			margin: theme.spacing(1),
		},
		textField: {
			margin: theme.spacing(1),
			width: "100%"
		},
		root: {
			padding: theme.spacing(2),
			margin: theme.spacing(1)
		},
	})
);

function InnerForm() {

	const history = useHistory();
	const classes = useStyles({});
	const [submitDialogOpen, setSubmitDialogOpen] = useState(false);

	const handleClose = (answer: Boolean, values: Process) => {
		setSubmitDialogOpen(false);
		if (answer === true) {
			submitProcess(values);
		}
	};

	const submitProcess = (values: Process) => {
		save_process_template(values);
		console.log(values);
		history.push('/View_Process_Templates');
	}
	return (
		<Formik
			initialValues={{
				title: "",
				activities: [
					new Activity(),
				],
			}}
			onSubmit={(values, actions) => {
				does_process_exist(values.title).then((does_exist) => {
					if (does_exist)
						setSubmitDialogOpen(true);
					else
						submitProcess(values);
				});
			}}
		>
			{({ values,
				errors,
				touched,
				handleChange,
				handleBlur,
				handleSubmit,
				isSubmitting }) => (
					<Form>
						<Grid
							container
							justify="center"
						>
							<TextField
								name="title"
								className={classes.textField}
								label="Process Title"
								margin="normal"
								variant="outlined"
								autoComplete="off"
								type="text"
								required
								onChange={handleChange}
								onBlur={handleBlur}
								value={values.title.length == 1 ? values.title.toUpperCase() : values.title}
								inputProps={{
									style: { fontSize: 30 }
								}}
								InputLabelProps={{
									style: { fontSize: 30 }
								}}

							/>
						</Grid>
						<FieldArray name="activities"
							render={(helpers) => (
								<div>
									{values.activities && values.activities.length > 0 ? (
										values.activities.map((activity, index) => (
											<Paper className={classes.root} key={index}>
												<Grid
													container
													justify="center"
												>
													<TextField
														autoComplete="off"
														name={`activities.${index}.title`}
														className={classes.textField}
														label="Activity Title"
														margin="normal"
														variant="outlined"
														type="text"
														required
														onChange={handleChange}
														onBlur={handleBlur}
														value={activity.title.length == 1 ? activity.title.toUpperCase() : activity.title}
													/>
													<TextField
														name={`activities.${index}.description`}
														id="outlined-basic"
														className={classes.textField}
														label="Description"
														margin="normal"
														multiline
														rows="3"
														variant="outlined"
														type="text"
														onChange={handleChange}
														onBlur={handleBlur}
														value={activity.description}
													/>
													<Grid
														justify="center"
														container

													>
														<IconButton
															className={classes.button}
															aria-label="add"
															color="default"
															onClick={() => helpers.push({ title: "", description: "" })}>
															<AddIcon />
														</IconButton>
														<IconButton
															className={classes.button}
															disabled={values.activities.length < 2}
															aria-label="add"
															color="default"
															onClick={() => helpers.remove(index)}>
															<RemoveIcon />
														</IconButton>
													</Grid>
												</Grid>
											</Paper>
										))
									) : null}
									<Grid
										justify="center"
										container
									>
										<Dialog
											open={submitDialogOpen}
											onClose={() => {
												handleClose(false, values);
											}}
											aria-labelledby="alert-dialog-title"
											aria-describedby="alert-dialog-description"
										>
											<DialogTitle id="alert-dialog-title">
												{"A process with this name already exists, do you want to overwrite it?"}
											</DialogTitle>
											<DialogActions>
												<Button
													onClick={() => {
														handleClose(false, values);
													}}
													color="primary"
												>
													No
          										</Button>
												<Button
													onClick={() => {
														handleClose(true, values);
													}}
													color="primary"
													autoFocus
												>
													Yes
          										</Button>
											</DialogActions>
										</Dialog>
										<Button
											variant="contained"
											className={classes.button}
											type="submit"
											color="primary"
											disabled={
												isSubmitting
											}
										>
											Save
							</Button>
									</Grid>
								</div>
							)} />
					</Form>
				)}
		</Formik>

	);
};



export default InnerForm;
