import React, { Component, FunctionComponent, useState } from 'react';
import ReactDOM from 'react-dom';
import './App.css';

import { Button, AppBar, Toolbar, IconButton, Typography, createStyles, makeStyles, Theme, Container } from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu';
import CreateProcess from './views/Create_Process_Form';
import ViewAllProcessInstances from './views/View_All_Process_Instances';
import CreateProcessInstance from './views/View_Process_Templates';
import ViewProcessInstance from './views/View_Process_Instance';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import CssBaseline from '@material-ui/core/CssBaseline';
const theme = createMuiTheme({
  overrides: {
    MuiCssBaseline: {
      '@global': {
        body: {
          backgroundColor: '#F5F5F5',
        },
      },
    },
  },
})

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      flexGrow: 1,
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
    },
    link: {
    }
  }),
);

function App() {
  const classes = useStyles({});
  return (
    <MuiThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" className={classes.title}>
              Netgain Boarding App
            </Typography>
            <Button component={Link} to="/" color="inherit">Home</Button>
            <Button component={Link} to="/View_Process_Templates" color="inherit">View Process Templates</Button>
            <Button component={Link} to="/Create_Process" color="inherit">Create Process</Button>
            <Button component={Link} to="/View_All_Process_Instances" color="inherit">View Process Instances</Button>
          </Toolbar>
        </AppBar>
        <Container
          style={{
            marginTop: theme.spacing(2),
          }}
        >
          <Switch>
            <Route path="/Create_Process">
              <CreateProcess />
            </Route>
            <Route path="/View_Process_Instance/:user_name/:process_instance_name" component={ViewProcessInstance} />
            <Route path="/View_Process_Templates">
              <CreateProcessInstance />
            </Route>
            <Route path="/View_All_Process_Instances" >
                <ViewAllProcessInstances />
            </Route>
            <Route path="/">
            <Typography variant="h2" >
              Home
            </Typography>
            </Route>
          </Switch>
        </Container>
      </Router>
    </MuiThemeProvider>
  );
}

export default App;
