import React from 'react';
import Button, { ButtonProps } from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { get_process_instance, create_process_instance } from '../backend/ts/database';
import { Process } from '../backend/ts/common_define';
import { withFormik, FormikProps, Form, FieldArray, Field, Formik, FormikComputedProps, FormikState, FormikConfig, FormikBag, FieldProps, FieldMetaProps } from "formik";
import AddIcon from '@material-ui/icons/Add';

interface Get_Name_Modal_Props {
    process_template: Process,
    //button_props: ButtonProps,
}


export default function Get_Name_Modal(props: Get_Name_Modal_Props) {
    const [open, setOpen] = React.useState(false);
    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose= () => {
        setOpen(false);
    };


    return (
        <div>
            {/* //TODO  make this more general
                eg get the what button should be from outside this component
            */}
            <Button variant="text"
                color="primary"
                startIcon={<AddIcon/>}
                onClick={handleClickOpen}
                ></Button>
            <Dialog
                fullWidth
                open={open}
                onClose={handleClose}
                aria-labelledby="form-dialog-title"
            >

                <DialogTitle id="form-dialog-title">Select user for new process instance</DialogTitle>
                <Formik
                initialValues={{ user_name: "" }}
                onSubmit={(values, actions) => {
                    setOpen(false);
                    console.log({ values, actions });
                    create_process_instance(props.process_template, values.user_name);
                    actions.setSubmitting(false);
                }}

                >{(props) =>  (
                    <Form>
                        <DialogContent>
                            <Field
                                name="user_name"
                            >{({field, form, meta}:FieldProps) => (
                                <TextField
                                    style={{ width: "100%" }}
                                    type="text"
                                    label="Name"
                                    {...field}
                                />
                            )}</Field>
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={handleClose} color="primary">Cancel</Button>
                            <Button onClick={() => { props.handleSubmit(); }} color="primary">Create</Button>
                        </DialogActions>
                    </Form>

                )}</Formik>
            </Dialog>
        </div>
    );
}
