import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { ActivityInstance } from '../backend/ts/common_define'

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      width: '30%',
    },
    button: {
      marginTop: theme.spacing(1),
      marginRight: theme.spacing(1),
    },
    actionsContainer: {
      marginBottom: theme.spacing(2),
    },
    resetContainer: {
      padding: theme.spacing(3),
    },
  }),
);

function getSteps() {
  return ['Aktivitet 1', 'Aktivitet 2', 'Aktivitet 3'];
}



interface Line_Stepper_Props {
  activities: Array<ActivityInstance>;
}

export default function VerticalLinearStepper(props: Line_Stepper_Props) {
  const classes = useStyles({});
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  const getStepContent = (step: number) =>{
    return props.activities[step].description;
  }

  const handleNext = () => {
    setActiveStep(prevActiveStep => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep(prevActiveStep => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  return (
    <div className={classes.root}>
      <Stepper activeStep={activeStep} orientation="vertical">
        {props.activities.map((activity, index) => (
          <Step key={activity.title}>
            <StepLabel>{activity.title}</StepLabel>
            <StepContent>
              <Typography>{getStepContent(index)}</Typography>
              <div className={classes.actionsContainer}>
                <div>
                  <Button
                    disabled={activeStep === 0}
                    variant="contained"
                    color="secondary"
                    onClick={handleBack}
                    className={classes.button}
                  >
                    Tillbaka
                  </Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleNext}
                    className={classes.button}
                  >
                    {activeStep === steps.length - 1 ? 'Klar' : 'Nästa'}
                  </Button>
                </div>
              </div>
            </StepContent>
          </Step>
        ))}
      </Stepper>
      {activeStep === steps.length && (
        <Paper square elevation={0} className={classes.resetContainer}>
          <Typography>Alla steg är avklarade, bra jobbat</Typography>
          <Button
            onClick={handleReset}
            className={classes.button}
            variant="contained"
            style={{ backgroundColor: '#33cc33' }}
          >
            Återställ
          </Button>
        </Paper>
      )}
    </div>
  );
}
