export type ProcessProp = {
    title: string;
    activities: Array<Activity>;
}

export type ProcessInstanceProps = {
    start_date: Date;
    user_name: string;
}

export class Process {
    title: string = "";
    description: string = "";
    activities: Array<Activity> = [];

    constructor(state?: ProcessProp) {
        if (state !== undefined) {
            this.title = state.title;
            this.activities = state.activities;
        }
    }
}

export class ProcessInstance extends Process {
    activities: Array<ActivityInstance> = [];
    start_date?: Date;
    done_date?: Date;
    step: number = 0;
    user_name?: string;
    constructor(props?: ProcessInstanceProps & ProcessProp){
        super(props)
        if(props !== undefined) {
            this.user_name = props.user_name;
            this.start_date = props.start_date;
        }
    }
}

export type ActivityProp = {
    title: string;
    description: string;
    subProcess: Process;
}

export class Activity {
    title: string = "";
    description: string = "";
    statuses: Array<Status> = [];
    automationSettings: Object = {};

    constructor(A_state?: ActivityProp) {
        if(A_state !== undefined){
            this.title = A_state.title;
            this.description = A_state.description;
        }
    }
}

export type StatusProp = {
    title: string;
}

export class Status {
    title: string = "";

    constructor(A_state?: StatusProp) {
        if(A_state !== undefined){
            this.title = A_state.title;
        }
    }
}
export type ActivityInstanceProp = {

}

enum ActivityInstanceState  {
    not_started = "NOT_STARTED",
    started = "STARTED",
    done = "DONE",
}

export class ActivityInstance extends Activity {
    start_date?: Date;
    end_date?: Date;
    state: ActivityInstanceState;
    constructor(props: ActivityProp) {
        super(props);
        this.state = ActivityInstanceState.not_started;
    }
}
